%  data=data7;

%开盘
%最高
%最低
%收盘
%成交量

 [data_r,data_c]=size(data);

    if data_r ~=5 
       error('Invalid argument provided. The length of the data shoulb be 5 ');
    end
 
    if data_c<200 %% 把数据弄成和分析数据一样，不管有没有那么长
               output_results=zeros(2,5);
               return;
    end
 %%   
 number1=0;effect_number1=0;
 number2=0;effect_number2=0;
 number3=0;effect_number3=0;
 number4=0;effect_number4=0;
 number5=0;effect_number5=0;
 number6=0;effect_number6=0;
 number7=0;effect_number7=0;
 
                for i=1:data_c-2
                
          %% 判断是不是强势反弹或者冲高回落
                     A=(data(4,i+1)-data(1,i+1))/data(4,i+1); %收盘-开盘 越大越好
                     B=(data(4,i+1)-data(3,i+1))/data(4,i+1); %收盘-最低 越大越好
                     C=(data(2,i+1)-data(4,i+1))/data(4,i+1); %最高-收盘 越大越不好 
                     rebound=10*(A+B-C);
                     if rebound >1.0       %强势反弹并且第二天上涨
                         number1=number1+1;
                         if (data(4,i+2)-data(4,i+1))>0
                         effect_number1=effect_number1+1;
                         end
                     elseif rebound <-1.5  %当天冲高回落
                         number2=number2+1;
                         if (data(4,i+2)-data(4,i+1))<0
                         effect_number2=effect_number2+1;
                         end
                     end
                     
          %% 判断当天涨停后或跌停后第二天的情况
                     
                     D=100*(data(4,i+1)-data(4,i))/data(4,i);
                     
                     if D >9    %是不是涨停
                         number3=number3+1;
                         
                         if data(4,i+2)-data(4,i+1)>0
                         effect_number3=effect_number3+1;
                         end
                     elseif D <-9  %是不是跌停后必跌
                         number4=number4+1;
                         
                         if data(4,i+2)-data(4,i+1)<0
                         effect_number4=effect_number4+1;
                         end
                     end
          %%   当天成交量大幅上涨后第二天上涨的情况
                     E=(data(5,i+1)-data(5,i))/data(5,i);
                     
                     if E >2.7    %是不是成交量大涨
                         number5=number5+1;
                         
                         if data(4,i+2)-data(4,i+1)>0
                         effect_number5=effect_number5+1;
                         end
                     end
                     
            %%  反弹&&成交量大增    
                     if rebound >1 && E >2 
                         number6=number6+1;
                         
                         if data(4,i+2)-data(4,i+1)>0
                         effect_number6=effect_number6+1;
                         end
                     end
             %%  大涨&&成交量大增
                    if D >8 && E >2.5 
                         number7=number7+1;
                         
                         if data(4,i+2)-data(4,i+1)>0
                         effect_number7=effect_number7+1;
                         end
                     end

                end
    
    
%% 计算概率

output_results(1,1)=effect_number1/number1; output_results(1,2)=effect_number2/number2; output_results(1,3)=effect_number3/number3; output_results(1,4)=effect_number4/number4; output_results(1,5)=effect_number5/number5;
output_results(1,6)=effect_number6/number6;output_results(1,7)=effect_number7/number7;

%% 计算当天的情况
                     output_results(2,1:7)=0;

                     if 10*(3*data(4,end)-data(1,end)-data(2,end)-data(3,end))/data(4,end) >1.5  %是不是强势反弹
                         output_results(2,1)=1;
                     elseif 10*(3*data(4,end)-data(1,end)-data(2,end)-data(3,end))/data(4,end) <-1.5  %是不是冲高回落
                         output_results(2,2)=1;    
                     end

                     
                     if  100*(data(4,end)-data(4,end-1))/data(4,end-1)>9    %是不是涨停
                         output_results(2,3)=1;
                     elseif 100*(data(4,end)-data(4,end-1))/data(4,end-1) <-9  %是不是跌停
                         output_results(2,4)=1;
                     end
                     

                     if (data(5,end)-data(5,end-1))/data(5,end-1) >2.7    %是不是成交量大涨
                         output_results(2,5)=1;
                     end
                     
                     if (10*(3*data(4,end)-data(1,end)-data(2,end)-data(3,end))/data(4,end) >1.5) && ( (data(5,end)-data(5,end-1))/data(5,end-1) >2.7   )
                         output_results(2,6)=1;
                     end
                     
                     if (100*(data(4,end)-data(4,end-1))/data(4,end-1)>8) && ( (data(5,end)-data(5,end-1))/data(5,end-1) >2.7   )
                         output_results(2,7)=1;
                     end

